$yellow: #ffde22;

.navigation {
    display: flex;
    justify-content: space-around;
    align-items: center;
    flex-wrap: wrap;
    width: 90%;
    margin: auto;
    font-family: 'Raleway', sans-serif;

    div {
        padding: 10px;
        margin: 6px;
        background-color: $yellow;
        width: 100px;
        text-align: center;
        text-transform: uppercase;
        border-radius: 5px;
        transition: background-color .3s, color .3s;

        a {
            color: black;
            text-decoration: none;
        }

        &:hover, a:hover{
            color: $yellow;
            background-color: black;
        }
    }


}

.title {

    text-align: center;
    font-family: 'Righteous', cursive;
    font-size: 5em;
    margin: 20px;
        span {
            color: #ff414e;
            text-decoration: overline #ff414e;
        }


}

.subtitle {

    text-align: center;
    font-family: 'Righteous', cursive;
    font-size: 3em;
    margin: 0;
        span {
            color: #ff414e;
            text-decoration: underline #ff414e;
        }


}

.row {
    display: flex;
    width: 90%;
    flex-wrap: wrap;
    margin: 10px auto;
    justify-content: space-around;
}

.paragraph {
    p {
        font-family: 'IBM Plex Serif', serif;
        font-size: 3em;
        text-align: center;
        background-color: coral;
        padding: 15px;
        border-radius: 6px;
        color: white;

        span {
            color: black
        }
    }

}

.card {
    display: flex;
    flex-direction: column;
    justify-content: flex-start;
    padding: 20px;
    background-color: silver;
    height: 400px;
    width: 350px;
    font-family: 'IBM Plex Serif', serif;
    text-align: center;

    h2 {
        background-color: coral;
        padding: 5px;
    }

    a {
        color: black;
        text-decoration: none;

        &:hover {
            color: #ff414e;

        }
    }
