@import url('https://fonts.googleapis.com/css?family=IBM+Plex+Serif|Raleway|Righteous&display=swap');
$orange: #ff8928;
$yellow: #ffde22;

.navigation {
    display: flex;
    justify-content: space-around;
    align-items: center;
    flex-wrap: wrap;
    width: 90%;
    margin: auto;
    font-family: 'Raleway', sans-serif;

    div {
        padding: 10px;
        margin: 6px;
        background-color: $yellow;
        width: 100px;
        text-align: center;
        text-transform: uppercase;
        border-radius: 5px;
        transition: background-color .3s, color .3s;

        a {
            color: black;
            text-decoration: none;
        }

        &:hover, a:hover{
            color: $yellow;
            background-color: black;
        }
    }


}

.title {

    text-align: center;
    font-family: 'Righteous', cursive;
    font-size: 5em;
    margin: 20px;
        span {
            color: #ff414e;
            text-decoration: overline #ff414e;
        }


}

.subtitle {

    text-align: center;
    font-family: 'Righteous', cursive;
    font-size: 3em;
    margin: 0;
        span {
            color: #ff414e;
            text-decoration: underline #ff414e;
        }


}

@media only screen and (max-width: 600px) {

    .navigation {
        display: flex;
        justify-content: space-around;
        align-items: center;
        overflow: scroll;
        flex-wrap: nowrap;
        width: 90%;
        margin: auto;
        font-family: 'Raleway', sans-serif;

        div {
            padding: 10px;
            margin: 6px;
            background-color: $yellow;
            width: 90px;
            text-align: center;
            text-transform: uppercase;
            border-radius: 5px;
            transition: background-color .3s, color .3s;

            a {
                color: black;
                text-decoration: none;
            }

            &:hover, a:hover{
                color: $yellow;
                background-color: black;
            }
        }


    }

  }

.row {
    display: flex;
    width: 90%;
    flex-wrap: wrap;
    margin: 10px auto;
    justify-content: space-around;
}

.paragraph {
    p {
        font-family: 'IBM Plex Serif', serif;
        font-size: 2em;
        text-align: center;
        background-color: coral;
        padding: 15px;
        border-radius: 6px;
        color: white;

        span {
            color: black
        }
    }

}

.paragraph2 {
    p {
        font-family: 'IBM Plex Serif', serif;
        font-size: 1em;
        text-align: center;
        background-color: coral;
        padding: 15px;
        border-radius: 6px;
        color: white;

        span {
            color: black
        }
    }

}

.card {
    display: flex;
    flex-direction: column;
    justify-content: flex-start;
    padding: 20px;
    background-color: silver;
    height: 400px;
    width: 330px;
    font-family: 'IBM Plex Serif', serif;
    text-align: center;

    h2 {
        background-color: coral;
        padding: 5px;
    }

    a {
        color: black;
        text-decoration: none;

        &:hover {
            color: #ff414e;

        }
    }
}

.smallcard {
    height: 350px;
    width: 300px;
    background-color: silver;
    padding: 10px;
    margin: 10px;
    display: flex;
    flex-direction: column;

    &:hover {
        outline: 3px solid #ffde22;
    }
}

.project {

    header {

        img{
            width: 100%;
            height: 270px;
        }
    }

    > div {
        background-color: coral;
        padding: 5px;
        color: white;
        font-weight: 900;
        font-family: 'IBM Plex Serif', serif;

    }

    footer {
        display: flex;
        justify-content: space-around;
        height: 100%;
        align-items: center;

        div {
            background-color: white;
            color: #ff414e;
            font-weight: 900;
            font-family: 'Raleway', sans-serif;
            height: 40px;
            width: 80px;
            text-align: center;
            margin-top: 10px;
            line-height: 40px;
            transition: background-color .4s;

            &:hover {
                background-color: #ff414e;
            }

            a {
                color: #ff414e;
                text-decoration: none;

                &:hover {
                    background-color: #ff414e;
                    color: white
                }
            }
        }
    }
}
